import React from 'react'
import {AiOutlineMenu,AiOutlineClose} from 'react-icons/ai'

const Navbar = () => {

    const [nav,setNav] = useState(false);
    const navHandler = () => {
        setNav(!nav);
    };
  return (
    <div className='w-full h-25 bg-[#00af9a] flex justify-between items-center'>
        <h1 className='text-white font-bold md:text-4x1 sm:3x1 text-xl p-3'>E-COMMERCE</h1>
        <ul className='hidden md:flex p-3'>
            <li className='text-white font-bold p-2 hover:bg-[#000300] cursor-pointer'>Home</li>
            <li className='text-white font-bold p-2 hover:bg-[#000300] cursor-pointer'>Cart</li>
            <li className='text-white font-bold p-2 hover:bg-[#000300] cursor-pointer'>Login</li>
            <li className='text-white font-bold p-2 hover:bg-[#000300] cursor-pointer'>Register</li>
        </ul>


        <div className='md:hidden'>

            {nav ? (
            <AiOutlineClose onClick={navHandler} className='text-[#000300] text-2xl px-2'/> 
            ):( 
            <AiOutlineMenu onClick={navHandler} className='text-[#000300] text-2xl px-2'/>)}
            
            
        </div>


        <div className='md:hidden fixed top-0 left-0 h-100% w-60 bg-[#000300]'>
        <h1 className='text-white font-bold md:text-4x1 sm:3x1 text-xl p-3'>E-COMMERCE</h1>
            <ul className='flex flex-col text-left p-3'>
                <li className='text-white font-bold p-2 hover:bg-[#2C2A2A] cursor-pointer'>Home</li>
                <li className='text-white font-bold p-2 hover:bg-[#2C2A2A] cursor-pointer'>Cart</li>
                <li className='text-white font-bold p-2 hover:bg-[#2C2A2A] cursor-pointer'>Login</li>
                <li className='text-white font-bold p-2 hover:bg-[#2C2A2A] cursor-pointer'>Register</li>
            </ul>





        </div>
    </div>
  )
}

export default Navbar
