import React, {useContext} from 'react'
import {AiOutlineMenu,AiOutlineClose} from 'react-icons/ai'
import { useEffect, useState } from "react";
import {Link} from 'react-router-dom'
import { GlobalContext } from '../GlobalContext/GlobalContext';


const Navbar = () => {

    const [nav,setNav] = useState(false);


    const {LoginStatus} = useContext(GlobalContext)


    const navHandler = () => {
        setNav(!nav);
    };
  return (
    <div className='w-full h-25 bg-[#000300] flex justify-between items-center'>
        <h1 className='text-white font-bold md:text-4x1 sm:3x1 text-xl p-3'>E-COMMERCE</h1>
        <ul className='hidden md:flex p-3'>
            <Link to='/'><li className='text-white font-bold p-2 hover:bg-[#2C2A2A] cursor-pointer'>Home</li></Link>
            <Link to="cart"><li className='text-white font-bold p-2 hover:bg-[#2C2A2A] cursor-pointer'>Cart</li></Link>
            <Link to="login"><li className='text-white font-bold p-2 hover:bg-[#2C2A2A] cursor-pointer'>Login</li></Link>
            <Link to="register"><li className='text-white font-bold p-2 hover:bg-[#2C2A2A] cursor-pointer'>Register</li></Link>
        </ul>


        <div className='md:hidden'>

            {nav ? (
            <AiOutlineClose onClick={navHandler} className='text-white text-4xl px-2'/> 
            ):( 
            <AiOutlineMenu onClick={navHandler} className='text-white text-4xl px-2'/>)}
            
            
        </div>


        <div className={nav ? `md:hidden fixed top-0 left-0 h-[100%] w-60 bg-[#000300] ease-in-out duration-300` : `hidden`}>
        <h1 className='text-white font-bold md:text-4x1 sm:3x1 text-xl p-3'>E-COMMERCE</h1>
            <ul className='flex flex-col text-left p-3'>
            <li className='text-white font-bold p-2 hover:bg-[#2C2A2A] cursor-pointer'>Home</li>
            <Link to="cart"><li className='text-white font-bold p-2 hover:bg-[#2C2A2A] cursor-pointer'>Cart</li></Link>
            <Link to="login"><li className='text-white font-bold p-2 hover:bg-[#2C2A2A] cursor-pointer'>Login</li></Link>
            <Link to="register"><li className='text-white font-bold p-2 hover:bg-[#2C2A2A] cursor-pointer'>Register</li></Link>
            </ul>





        </div>
    </div>
  )
}

export default Navbar
