import './App.css';
import Navbar from './components/Navbar';
import {BrowserRouter,Route,Routes} from "react-router-dom";
import Home from './pages/Home'
import Login from './pages/Login';
import Register from './pages/Register';
import Cart from './pages/Cart';
import Cookie from "js-cookie";
import axios from 'axios';
/* import React, {useEffect, useNavigate} from "react" */ //BUG
import React, {useEffect} from "react" 
import { GlobalContextProvider, GlobalContext } from './GlobalContext/GlobalContext';
import { useContext } from 'react';


function App() {
  /* const navigate = useNavigate(); */ //BUG


  const {IsLoggedIn} = useContext(GlobalContext);

  console.log(IsLoggedIn)

  const token = Cookie.get("jwt_token")
  console.log(Cookie.get("jwt_token"));

  useEffect(() => {

    axios
    .post(
      "http://localhost:5000/api/user/verify_account",
      { token },
      { withCredentials: true }
    )
    .then((res) => {
      console.log(res);
      if (!res.data.status) {
        Cookie.remove("jwt-token")
        /* navigate("/login") */ //BUG



      } else {
          console.log(res.data)
      }
    })
    .catch((err) => {
      console.log(`Request err: ${err}`);
    });
}, );


  return (
    <GlobalContextProvider>
    <div className="App">


      <BrowserRouter>
      <Navbar />
        <Routes>
          <Route path='/' element={<Home />}/>
          <Route path="cart"  element={<Cart />}/>
          <Route path='login' element={<Login />}/>
          <Route path='register' element={<Register />}/>
        </Routes>
      </BrowserRouter>
    </div>
    </GlobalContextProvider>
  );
}



export default App;
